import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Thumbnail from '../../components/thumbnail';
import artbashThumb from '../../assets/artbash/poster-1main.png';
import './work.scss';

const Work = () => {
    const works = useSelector((state) => state.work.work);

    return (
        <div className='work'>
            <Link to='/' className="thumb">
                <div className="thumb-title">
                    <h2>Promoting Freshmen Exhibition</h2>
                    <p>2022</p>
                </div>
                <ul className='taglist'>
                    <li>identity</li>
                    <li>poster</li>
                </ul>
                <div className="thumb-image">
                    <img src={artbashThumb} />
                </div>
            </Link>
            {Object.keys(works).map((work, i) => 
                <Link className='thumb' key={i}>
                    <div className="thumb-title">
                        <h2 className='project-title'>{works[work].title}</h2>
                        <p>{works[work].year}</p>
                    </div>
                    <ul className='taglist'>
                        {works[work].tags.map(tag => <></>)}
                    </ul>
                    <div className="thumb-image">
                        <img />
                    </div>
                </Link>
            )}
        </div>
    );
};

export default Work;
