import React from "react";
import './about.scss';

import {ReactComponent as Email} from '../../assets/icons/envelope.svg';
import {ReactComponent as Linkedin} from '../../assets/icons/linkedin.svg';
import {ReactComponent as Github} from '../../assets/icons/github.svg';

const About = () => {
    return (
        <div className="about">
            <section>
                <div className='profile'>
                    <img id='icon1' src={require('../../assets/about/profile1.png')} />
                    <img id='icon2' src={require('../../assets/about/profile2.jpg')} />
                </div>
                <p className="intro">
                    Hello! I'm Soomin, a designer based in Chicago. I'm currently a senior studying in the School of Art Institute of Chicago.
                    <br/><br/>
                    In my free time, I enjoy watching sci-fi movies and brewing coffee.
                </p>
            </section>

            <section>
                <h2>More about me...</h2>
                <div className="cols">
                    <div>

                    </div>
                </div>
            </section>

            <section>
                <h2>Contact</h2>
                <div><Email /><p>soomin2c@gmail.com</p></div>
                <div><Linkedin /><p>LinkedIn</p></div>
                <div></div>

                <Linkedin /> LinkedIn
                <Github /> GitHub
            </section>
        </div>
    );
};

export default About;
